mapper.select:
select DISTINCT oc.ID as id, oc.OPENID as openId, oc.CARNUMBER as carNum, oc.ENGINENUM as engineNum, oc.IDENTNUM as identNum, oc.EMISSIONSTD as emissionStd, oc.FLEETNAME as fleetName,oc.REGDATE as regDate,oc.DEPARTMENT as department, oc.IS_DELETED as isDeleted, oc.IS_ENABLED as isEnabled, oc.USER_CREATE as userCreate, oc.USER_MODIFIED as userModified, oc.GMT_CREATE as gmtCreate, oc.GMT_MODIFIED as gmtModified, decode(f.ATTACHMENT,null,'false','true') as hasDrivingLicense from SYS_OPENID_CARNUMBER oc left join FG_CARYB_FILE@SUPC f on oc.CARNUMBER = f.CAR_NO
and oc.OPENID = #{openId}
and oc.IS_DELETED = 0

select DISTINCT oc.ID, oc.OPENID, oc.CARNUMBER, oc.ENGINENUM, oc.IDENTNUM, oc.EMISSIONSTD, oc.FLEETNAME,oc.REGDATE,oc.DEPARTMENT, oc.IS_DELETED, oc.IS_ENABLED, oc.USER_CREATE, oc.USER_MODIFIED, oc.GMT_CREATE, oc.GMT_MODIFIED from SYS_OPENID_CARNUMBER oc left join SYS_MOBILE_OPENID mo on oc.OPENID = mo.OPENID
and mo.MOBILE = #{mobile}
and oc.IS_DELETED = 0

select oc.ID, oc.OPENID, oc.CARNUMBER,oc.ENGINENUM, oc.IDENTNUM, oc.EMISSIONSTD, oc.FLEETNAME,oc.REGDATE, oc.DEPARTMENT, oc.IS_DELETED, oc.IS_ENABLED, oc.USER_CREATE, oc.USER_MODIFIED, oc.GMT_CREATE, oc.GMT_MODIFIED from SYS_OPENID_CARNUMBER oc
and oc.OPENID = #{openId}
and oc.CARNUMBER = #{carNum}
and oc.IS_DELETED = 0

select count(*) from SYS_OPENID_CARNUMBER oc


        and oc.OPENID = #{openId}


        and oc.CARNUMBER = #{carNum}

    and oc.IS_DELETED = 0
    and oc.IS_ENABLED = 1

select oc.CARNUMBER as carNum from SYS_OPENID_CARNUMBER oc
and oc.OPENID = #{openId}
and oc.IS_DELETED = 0 and oc.IS_ENABLED = 1

mapper.update:
update SYS_OPENID_CARNUMBER oc
set oc.IS_DELETED = 1


        and oc.CARNUMBER = #{carNum}


        and oc.OPENID = #{openId}

    and oc.IS_DELETED = 0

update SYS_OPENID_CARNUMBER oc
set oc.IS_ENABLED = #{isEnable},
    oc.GMT_MODIFIED = #{gmtModified}


        and oc.CARNUMBER = #{carNum}


        and oc.OPENID = #{openId}

    and oc.IS_DELETED = 0

mapper.select:
select count(*) from SYS_OPENID_CARNUMBER oc


        and oc.CARNUMBER = #{oldCarNum}


        and oc.OPENID = #{openId}

    and oc.IS_DELETED = 0

select distinct a.PLATENO as carNum, a.CROSSTIME as inOutTime,
decode(a.CARINOUTSTRING, '0', '入场', '1', '出场', a.CARINOUTSTRING) as inOutType
from T_ZZYY_RC_CAR_HIK@rcdb a
inner join SYS_OPENID_CARNUMBER b
on b.OPENID = #{openId}
and b.IS_DELETED = 0
and a.PLATENO = b.CARNUMBER
where a.CROSSTIME  >=  #{startTime}
and a.CROSSTIME  <=  #{endTime}
order by a.PLATENO, a.CROSSTIME

select g.CLSBDH as clsbdh, g.FDJH as fdjh
from SYS_GSCAR_INFO g


        and g.HPHM = #{carNum}

select oc.CARNUMBER as carNum, oc.IS_ENABLED as isEnable from SYS_OPENID_CARNUMBER oc
and oc.OPENID = #{openId}
and oc.IS_DELETED = 0

select mo.ID, r.ROLE_CODE, r.ID as ROLEID from SYS_OPENID_CARNUMBER oc left join SYS_MOBILE_OPENID mo on oc.OPENID = mo.OPENID left join SYS_USER_ROLE ur on mo.ID = ur.MOBILE_OPENID_ID left join SYS_ROLE r on ur.ROLE_ID = r.id
and oc.CARNUMBER = #{carNum}
and oc.OPENID = #{openId}
and oc.IS_DELETED = 0 and r.IS_DELETED = 0

select * from ( select cf.BUMEN as bumen, cf.CAR_NUMBER as carNumber, cf.CAR_VIN_NUMBER as carVinNumber, cf.ENGINE_NUMBER as engineNumber, cf.PAIF as paif, cf.CROSSTIME as crossTime, cf.FORECAST_TIME as forecastTime, cf.ZCRQ as zcrq, cf.CARINOUTSTRING as carInOutString, cf.ENTRANCENAME as entranceName, cf.HWMC as hwmc, cf.YSL as ysl, cf.CDMC as cdmc, cf.VEHICLEURL as vehicleUrl, rownum as id from ( SELECT a.BUMEN, a.FORECAST_TIME, a.CAR_NUMBER, a.CAR_VIN_NUMBER, a.ENGINE_NUMBER, a.PAIF, a.HWMC, a.YSL, a.CDMC, a.ZCRQ, b.CARINOUTSTRING, b.CROSSTIME, b.ENTRANCENAME, b.VEHICLEURL FROM report.CDPINFO a, (SELECT * FROM (SELECT "PLATENO", "CROSSTIME", "VEHICLEURL", DECODE ("CARINOUTSTRING", '0', '入场', '1', '出场', "CARINOUTSTRING") "CARINOUTSTRING", "RELEASEMODENAME", "PARKNAME", "ENTRANCENAME", RANK () OVER ( PARTITION BY plateno, TRUNC ( TO_DATE ( crosstime, 'yyyy-MM-dd hh24:mi:ss')) ORDER BY crosstime ASC) seq FROM report.rcview
AND "PARKNAME" IN ('杨锦公路', '玖隆桥洞') AND ( ( DECODE ("CARINOUTSTRING", '0', '入场', '1', '出场', "CARINOUTSTRING") = '入场' AND releasemodename != '禁止放行') OR DECODE ("CARINOUTSTRING", '0', '入场', '1', '出场', "CARINOUTSTRING") = '出场') and CROSSTIME like concat(#{startDate}, '%')
and PLATENO = #{carNum}
) WHERE seq = 1 ) b WHERE a.car_number = b.plateno AND REPLACE (SUBSTR (b.CROSSTIME, 0, 10), '-', '') = a.FORECAST_TIME order by b.CROSSTIME desc ) cf ) where id <= #{end} and id >= #{start}

SELECT count(*) FROM report.CDPINFO a, (SELECT * FROM (SELECT "PLATENO", "CROSSTIME", "VEHICLEURL", DECODE ("CARINOUTSTRING", '0', '入场', '1', '出场', "CARINOUTSTRING") "CARINOUTSTRING", "RELEASEMODENAME", "PARKNAME", "ENTRANCENAME", RANK () OVER ( PARTITION BY plateno, TRUNC ( TO_DATE ( crosstime, 'yyyy-MM-dd hh24:mi:ss')) ORDER BY crosstime ASC) seq FROM report.rcview
AND "PARKNAME" IN ('杨锦公路', '玖隆桥洞') AND ( ( DECODE ("CARINOUTSTRING", '0', '入场', '1', '出场', "CARINOUTSTRING") = '入场' AND releasemodename != '禁止放行') OR DECODE ("CARINOUTSTRING", '0', '入场', '1', '出场', "CARINOUTSTRING") = '出场') and CROSSTIME like concat(#{startDate}, '%')
and PLATENO = #{carNum}
) WHERE seq = 1 ) b WHERE a.car_number = b.plateno AND REPLACE (SUBSTR (b.CROSSTIME, 0, 10), '-', '') = a.FORECAST_TIME order by b.CROSSTIME desc

mapper.update:
update FG_CARYB_FILE@SUPC a
set a.ATTACHMENT = #{drivingLicense}
where a.CAR_NO = #{carNum}

mapper.select:
select a.CARNUMBER as carNum, a.START_DATE as startDate,
a.EXPIRE_DATE as expireDate, a.GMT_CREATE as gmtCreate,
a.GMT_MODIFIED as gmtModified, a.VIOLATION_TIMES as violationTimes
from SYS_CARNUM_FROZEN a


        and a.CARNUMBER = #{carNum}


        and a.FROZEN_STATUS = #{frozenStatus}

update.foreach:
update SYS_CARNUM_FROZEN set FROZEN_STATUS = #{carnumfrozen.frozenStatus}, START_DATE = #{carnumfrozen.startDate},
  EXPIRE_DATE = #{carnumfrozen.expireDate}, GMT_MODIFIED = #{carnumfrozen.gmtModified}, VIOLATION_TIMES = #{carnumfrozen.violationTimes}
where ID = #{carnumfrozen.id}
