mapper.select:
select a.msgCode from sys_mobile_msgcode a
and a.mobile = #{mobile}
and a.status = 1

mapper.insert:
insert into sys_mobile_msgcode(
  id, msgCode, mobile,status,gmt_create,gmt_modified
)
values (MOBILE_MESCODE_SEQ.nextval, #{mesCode,jdbcType=VARCHAR}, #{mobile,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR},
   #{gmtCreate,jdbcType=TIMESTAMP},#{gmtModified,jdbcType=TIMESTAMP}
)

mapper.update:
update sys_mobile_msgcode
set status = #{status,jdbcType=VARCHAR},
gmt_modified = #{gmtModified,jdbcType=TIMESTAMP}
where mobile = #{mobile,jdbcType=VARCHAR}

mapper.select:
select a.m from sys_mobile_msgcode a


        and a.mobile = #{mobile}

    and a.status = 1
