mapper.select:
select DISTINCT mo.ID, mo.MOBILE, mo.OPENID, mo.USER_CREATE, mo.GMT_CREATE
 from SYS_MOBILE_OPENID mo


        and mo.MOBILE = #{mobile}


        and mo.OPENID = #{openId}

select mo.ID, mo.MOBILE, mo.OPENID, mo.USER_CREATE, mo.GMT_CREATE, ur.ROLE_ID from SYS_MOBILE_OPENID mo left join SYS_USER_ROLE ur on mo.ID = ur.MOBILE_OPENID_ID
and mo.MOBILE = #{mobile}
and mo.OPENID = #{openId}

mapper.update:
update SYS_MOBILE_OPENID mo
set mo.MOBILE = #{mobile}
where mo.OPENID = #{openId}
